mapper.select:
SELECT * FROM portal_info WHERE del_flag = #{DEL_FLAG_NORMAL}
AND userId = #{userId}
AND description LIKE '%'||#{description}||'%' '%'+#{description}+'%' CONCAT('%', #{description}, '%')
ORDER BY type, sort, update_date DESC

mapper.update:
UPDATE portal_info SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
